//Signup
import axios from 'axios';
const SET_SIGNUP_PENDING = 'SET_SIGNUP_PENDING';
const SET_SIGNUP_SUCCESS = 'SET_SIGNUP_SUCCESS';
const SET_SIGNUP_ERROR = 'SET_SIGNUP_ERROR';
export function signup(user_name,email,password,password_confirm,address) { //reducer thuc hien action login xu ly du lieu lay qua
  return dispatch => {
    dispatch(setSignupPending(true));
    dispatch(setSignupSuccess(false));
    dispatch(setSignupError(null));

    callSignupApi(user_name,email,password,password_confirm,address,error=> {
      dispatch(setSignupPending(false));//tra ve cho form
      if (!error) {
        dispatch(setSignupSuccess(true));//tra ve cho form
      } else {
        dispatch(setSignupError(error));//tra ve cho form
      }
    });
  }
}


function setSignupPending(isSignupPending) {
  return {
    type: SET_SIGNUP_PENDING,
    isSignupPending
  };
}

function setSignupSuccess(isSignupSuccess) {
  return {
    type: SET_SIGNUP_SUCCESS,
    isSignupSuccess
  };
}

function setSignupError(signupError) {
  return {
    type: SET_SIGNUP_ERROR,
    signupError
  }
}

function callSignupApi(user_name,email,password,password_confirm,address,callback) {
   if(password==password_confirm) {
  setTimeout(() => {
   axios.post(`http://127.0.0.1:3000/users`, {user_name:user_name,
   email:email,
   password:password,
   address:address })
      .then(res => {
        console.log('RES')
        console.log(res);
        console.log(res.data);
        if(res.data.length>0) 
        {    axios.get(`http://127.0.0.1:3000/users`)
          .then(res => {
            if(res.data.length>0)
          {
            localStorage.setItem('allUser',JSON.stringify(res.data))
          }
        })

            return callback(null);
        }
        else return callback(new Error("Invalid any field to create a new user"))
      })
     
  }, 1000);

}
else {
  return callback(new Error("Password and password confirm is not similar please enter again to signup!!!"))
}

}
 
var signup_state={
  isSignupPending:false,
  signupError:null,
  isSignupSuccess:false,
}
export default function signup_reducer(state =signup_state, action) {
  if(action.type==' SET_SIGNUP_PENDING')
  {
      let newState={...state};
      newState.isSignupPending=action.isSignupPending;
      return newState;
  }
  if(action.type=='SET_SIGNUP_SUCCESS')
  {  let newState={...state};
    newState.isSignupSuccess=action.isSignupSuccess;
    alert("singup successfully");
    return newState;

  }
  if(action.type=='SET_SIGNUP_ERROR')
  {  let newState={...state};
    newState.signupError=action.signupError;
    return newState;

  }
      return state;
  
}
