import React from 'react';
import Dashboard from './Dashboard';
import { connect } from 'react-redux';
class ManagerUser extends React.Component  {
  render(){
      return (
         <div>
          <Dashboard></Dashboard>
          <div>
              
              <div class="col-xs-9 col-sm-9 col-md-9 col-lg-9">
                 <div >
                   
                    <div >
                        <h2 style={{fontFamily:"'Times New Roman', Times, serif"}}>Manager Users</h2>
                        <hr style={{width:"300px",marginLeft:"7px",marginTop:"5px",backgroundColor:"#000000"}}></hr>
                    </div >
                    
                    <button type="button" class="btn btn-primary"><Link to="./newuser" >Add User</Link></button>
                       
                    <table class="table table-striped" style={{fontSize:"20px",fontFamily:"Times New Roman', Times, serif"}}>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Address</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>


                    </tbody>
                    </table>
                </div>
            </div>
              
            
      </div>
      </div>
  );
}
}
export default ManagerUser;
