import React from 'react';
import AdminDashboard from './AdminDashboard';
import { connect } from 'react-redux';
import {Link} from 'react-router-dom';
import {getAllUser} from '../../redux/user_reducer';
class AdminUser extends React.Component  {
    constructor(props)
    {
        super(props);
        this.state = {
          showMore: false,
          finish:4
        }
  }
    handleClick() {
      this.setState({showMore: true})
      this.setState((prevState) => ({
      finish: prevState.finish + 4
      }));
    }
    componentWillMount()
    {
       this.props.getAllUser();
    }
  render(){
      let{users=[]}=this.props;
      const numberOfItems = this.state.showMore ? this.state.finish : 4
      return (
          <div>
              <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
               <AdminDashboard></AdminDashboard>

              </div>
      
                    <div className="col-xs-9 col-sm-9 col-md-9 col-lg-9">
                        <div >
                            <div >
                                <h2 style={{fontFamily:"'Times New Roman', Times, serif",marginLeft:"10px"}}>Admin>Danh sách người dùng </h2>
                                <hr style={{width:"800px",marginLeft:"7px",marginTop:"5px",backgroundColor:"#000000"}}></hr>
                            </div >
                            
                            <p style={{fontSize:"32px",fontFamily:"'Times New Roman', Times, serif",marginLeft:"40px",marginTop:"30px"}}>Danh sách người dùng</p>
                            <input className="form-control" type="text" placeholder="Tìm kiếm" style={{width:"170px",marginLeft:"620px",marginTop:"20px"}}></input>
                            
                            <div className="row" style={{marginTop:"20px"}}>
                                
                                <table className="table table-striped" style={{fontSize:"20px",fontFamily:"Times New Roman', Times, serif"}}>
                            <thead>
                                <tr>
                                    <th>STT</th>
                                    <th>UserName</th>
                                    <th>Email</th>
                                    <th>Quyền hạn</th>
                                    <th>Hành Động</th>
                                </tr>
                            </thead>
                            <tbody>
                             {users.length>0 &&
                              list_order.slice(0, numberOfItems).map((item,i)=>
                              {
                              return(
                               <tr>
                                <td>{i} </td>
                                <td>{item.user_name}</td>
                                <td>{item.email}</td>
                                { item.role==1 &&
                                 <td>Người dùng</td>   
                                }
                               { item.role==2 &&
                                <td>Chủ Shop</td>   
                                 }
                              { item.role==3 &&
                              <td>Quản trị viên</td>   
                                }
                          <td> 
                              
                            <button className="btn" style={{backgroundColor:"#A52A2A"}}onClick={e =>{
                             
                             
                            }
                            }><Link to="">XEM</Link></button>
                       

                        <button className="btn" style={{backgroundColor:"#A52A2A"}}onClick={e =>{
                             this.props.cancelOrder(item.id);
                             
                         } }>Xóa</button>
                    
                            </td>

                               </tr>
                                
                              )
                              
                              
                              })

                             }   
                            </tbody>
                            </table>
                            <div className="button" style={{marginLeft:"600px"}}>
        <button onClick={()=> this.handleClick()}  type="button" className="btn btn-default" style={{color:"black",backgroundColor:"brown"}}>Xem thêm</button>
        </div>          
                                </div>
                            </div>
                            
                            
                        </div>
                    </div>
   
   
  );
}
}
const mapStateToProps = state => {
    return { users:state.userState.users
        }
    }
    const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
        return {
            getAllUser:()=>dispatch(getAllUser())
      
        };
        }
    

export default connect(mapStateToProps,mapDispatchToProps) (AdminUser);
