import axios from 'axios';

const GET_ORDER_DETAIL='GET_ORDER_DETAIL';   
const GET_PRODUCT='GET_PRODUCT';
export function getOrderDetail(id) {
    return dispatch => {
       callDetailApi(id,data => {
        dispatch(setDetail(data));
        //tra ve cho form
          
      });
    }
  }

function setDetail(orderdetail) {
    console.log(orderdetail);
    return {
      type: GET_ORDER_DETAIL,
     orderdetail
    };
  }
  
function  callDetailApi(id,callback) {
  axios.get(`http://127.0.0.1:3000/order_detail?order_id=`+id)
  .then(res => {
  const  orders = res.data;
    if (res.data!=null)
     {
        return callback(orders)
    }
      
  }
  )

}
export function getProduct(id) {
  return dispatch => {
     callApi(id,data => {
      dispatch(set(data));
      //tra ve cho form
        
    });
  }
}


function set(product) {
  return {
    type: GET_PRODUCT,
    product
  };
}


function  callApi(id,callback) {
  var list_product=[];
axios.get(`http://127.0.0.1:3000/order_detail?order_id=`+id)
.then(res => {
const  orders = res.data;
  if (res.data.length>0)
   { console.log(orders);
     console.log(orders.length);
     if (orders.length > 0) {
      for (var i = 0; i < orders.length; i++) {
        axios.get(`http://127.0.0.1:3000/products?id=`+orders[i].product_id)
        .then(res => {
          const product=res.data;
          if(res.data.length>0)
           
           list_product.push(product[0]);

        })
      }
      return callback(list_product)
  }
   } 
}
)

}

