 var data=JSON.parse(localStorage.getItem('OrderDetail'));
 var data1=JSON.parse(localStorage.getItem('ProductOrderDetail'));
 var initialState =
 { orderdetail:data ? data : [],
  product:data1? data1:[],
 }

   
export default function order_detail_reducer(state =initialState, action) {
  if(action.type=='GET_ORDER_DETAIL')
  {
      let newState={...state};
      newState.orderdetail=action.orderdetail
      localStorage.setItem('OrderDetail', JSON.stringify(newState.orderdetail));
      return newState;
      
  }
  if(action.type=='GET_PRODUCT')
  {
    let newState={...state};
    newState.product=action.product;
    console.log("dien roi nghe m");
    console.log(newState.product);
    localStorage.setItem('ProductOrderDetail', JSON.stringify(newState.product));
    return newState;
  }
  else return state;
}
