import React, { Component } from 'react';
import { Link ,Redirect} from "react-router-dom";
import { connect } from 'react-redux';
class CartResult extends Component {
    constructor(props)
    {
        super(props);

    }
    render() {
        var { cart } = this.props;
        let{  isLogingSuccess}=this.props;
        return (
            <tr>
                <td colSpan="3"></td>
                <td>
                    <h4>
                        <strong>Tổng Tiền</strong>
                    </h4>
                </td>
                <td>
                    <h4>
                        <strong  >{this.showTotalAmount(cart)}Đ</strong>
                    </h4>
                </td>
                <td colSpan="3">

                    <button type="button" onClick={e=>{
                     isLogingSuccess!=true &&
                    <Redirect to="/login"></Redirect>
                    isLogingSuccess==true &&
                    <Redirect to="/order"></Redirect>
                    }} className="btn btn-primary waves-effect waves-light" style={{backgroundColor:"#A52A2A"}}><Link to='/order'>Order</Link>

                    </button>
                </td>
            </tr>
        );
    }

    showTotalAmount = (cart) => {
        var total = 0;
        if (cart.length > 0) {
            for (var i = 0; i < cart.length; i++) {
                total += cart[i].product.price * cart[i].quantity;
            }
        }
        return total;
    }


}
const mapStateToProps = state => {

    return {

        isLogingSuccess:state.loginState.isLogingSuccess
    }
}
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
    return {

    };
    }
export default connect(mapStateToProps,mapDispatchToProps)(CartResult);
