import React from 'react';
import Dashboard from './Dashboard';
import { connect } from 'react-redux';
import {Link} from "react-router-dom";
import { getAllSubCategory} from '../../redux/home_reducer';
import { newproduct} from '../../redux/product_reducer';
import axios from 'axios';
class AddProduct extends React.Component  {
    constructor(props)
    {
       
            super(props);
            this.state ={
                file: null
            };
            this.onFormSubmit = this.onFormSubmit.bind(this);
            this.onChange = this.onChange.bind(this);
    }
    componentWillMount()
    {
        this.props.getAllSubCategory();
        
    }
    change(event)
    {
      this.setState({category_name: event.target.value});
    }
    onFormSubmit(e){
        e.preventDefault();
        const formData = new FormData();
        formData.append('myImage',this.state.file);
        this.setState({product_image:this.state.file.name});
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        };
        axios.post("http://127.0.0.1:3000/image",formData,config)
            .then((response) => {
                alert("The file is successfully uploaded");
            }).catch((error) => {
        });
    }
    onChange(e) {
        this.setState({file:e.target.files[0]});
       
    }
     
  render(){
      let {category=[],isAddingsuccess}=this.props;
      let{product_name,product_details,price,product_image,quantity,category_name,hot_product=false}=this.state;
     
      if(isAddingsuccess) alert("add successfully");
      return (
     <div>     
    <Dashboard></Dashboard>
       <div >
           <div class="col-xs-9 col-sm-9 col-md-9 col-lg-9">
                <div >
                    <h3 style={{fontFamily:"'Times New Roman', Times, serif",color:"black"}} > Add Product</h3>
                    <hr style={{width:"1000px",marginLeft:"7px",marginTop:"5px",backgroundColor:"#000000"}}></hr>
                </div >
                <div>
                    <div class="col-xs-1 col-sm-1 col-md-1 col-lg-1" style={{marginLeft:"100px"}}>
                   
                 <h1>Product Image</h1>
                <input type="file" name="myImage" onChange= {this.onChange} />
                 <button type="submit" onClick={this.onFormSubmit}>Upload</button>
           
                 
                    </div>
                    <div class="col-xs-7 col-sm-7 col-md-7 col-lg-7" style={{marginLeft:"200px",marginTop:"13px"}}>
                        <div class="row">
                            
                            <div class="col-xs-4 col-sm-4 col-md-4 col-lg-4">
                            <select className="form-control"  onChange={event=>this.setState({value: event.target.value,by:"Category"})} >
            
                            {  this.props.category.length>0 &&
                                this.props.category.map(function(item, i){
                                return(<option>{item.category_name}</option>
                                   
                                  )
                                 })
                             }
            
            } 
            </select>
                            </div>
                            <p style={{fontFamily:"'Times New Roman', Times, serif",fontSize:"20px"}}>List Category</p>
                        </div>
                    </div>
                    
                    <div class="col-xs-7 col-sm-7 col-md-7 col-lg-7" style={{marginTop:"80px"}}>
                    <div class="row">
                                <div class="col-xs-5 col-sm-5 col-md-5 col-lg-5">
                                    <input type="text"  class="form-control" placeholder="ProductName" onChange={e => this.setState({product_name: e.target.value})} value={product_name}></input>    
                                </div>
                                <p style={{fontFamily:"'Times New Roman', Times, serif",fontSize:"21px"}}>Product Name</p>
                        </div><br/>
                        <div class="row">
                                <div class="col-xs-5 col-sm-5 col-md-5 col-lg-5">
                                    <input type="text"  class="form-control" placeholder="Product Detail" onChange={e => this.setState({product_details: e.target.value})} value={product_details}></input>    
                                </div>
                                <p style={{fontFamily:"'Times New Roman', Times, serif",fontSize:"21px"}}>Product Detail</p>
                        </div><br/>
                        <div class="row">
                                <div class="col-xs-5 col-sm-5 col-md-5 col-lg-5">
                                    <input type="text"  class="form-control" placeholder="Price" onChange={e => this.setState({price: e.target.value})} value={price}></input>    
                                </div>
                                <p style={{fontFamily:"'Times New Roman', Times, serif",fontSize:"21px"}}>Price</p>
                        </div><br/>
                        <div class="row">
                                <div class="col-xs-5 col-sm-5 col-md-5 col-lg-5">
                                    <input type="text"  class="form-control" placeholder="Quantity" onChange={e => this.setState({quantity: e.target.value})} value={quantity}></input>    
                                </div>
                                <p style={{fontFamily:"'Times New Roman', Times, serif",fontSize:"21px"}}>Quantity</p>
                        </div><br/>
                    </div>
                </div>
               
                 </div>
             <button type="button" class="btn btn-primary" style={{marginLeft:"670PX",marginTop:"25px",backgroundColor:"brown"}} onClick={e=>{this.props.newproduct(product_name,product_details,price,product_image,quantity,category_name,hot_product)}}>Add Product</button>
            
        </div>
        </div>       
           
  );
}
}
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
    return {
    //   isSignupPending: state.signupState.isSignupPending,
    //   isSignupSuccess: state.signupState.isSignupSuccess,
    //   signupError: state.signupState.signupError
   category:state.homeState.allSubCategory,
   isAddingsuccess:state.productState.isAddingsuccess
    };
    }
    
    const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
    return {
     getAllSubCategory:()=>dispatch(getAllSubCategory()),
     newproduct:(product_name,product_details,price,product_image,quantity,category_name,hot_product)=>dispatch(newproduct(product_name,product_details,price,product_image,quantity,category_name,hot_product))
    };
    }
    export default connect(mapStateToProps, mapDispatchToProps)(AddProduct);
