import axios from 'axios';
const GET_PRODUCT_FILTER = 'GET_PRODUCT_FILTER';
export function sort(by,value) {
    console.log("by");
    console.log(by);
    console.log("value");
    console.log(value);
  return dispatch => {
     callApi(by,value,data => {
         dispatch(set(data));//tra ve cho form
        
    });
  }
}
function set(data) {
  return {
    type: GET_PRODUCT_FILTER,
    data
  };
}
function callApi(by,value, callback) {
  var id=null; 
  if(by=="Category")
    { 
  axios.get(`http://127.0.0.1:3000/categories?category_name=`+value)
  .then(res => {
   
    if (res.data.length>0)
     { let cate=res.data;
      
      id=cate[0].id;
      axios.get(`http://127.0.0.1:3000/products?category_id=`+id)
      .then(res => {
      const  data= res.data;
        if (res.data!=null)
          { 
          return callback(data);//not error
         } 
       }
      )
    
      } 
 
  }

  )}

}


var data = JSON.parse(localStorage.getItem('Filter'));
var initialState = data ? data : [];
export default function filter_reducer(state =initialState, action) {
  if(action.type=='GET_PRODUCT_FILTER')
  {  console.log(action.data);
    let newState={...state};
    newState.data=action.data
    localStorage.setItem('Filter', JSON.stringify(newState));
    console.log(newState);
    return newState;
  }

  else return state;
}
