import React from 'react';
import Dashboard from './Dashboard';
import { connect } from 'react-redux';
import {Link} from "react-router-dom";
import {  getAllUser,getUser,deleteUser} from '../../redux/user_reducer';
class ManagerUser extends React.Component  {
    constructor(props)
    {
        super(props);
        this.state={};
    }
    componentWillMount()
    {
        //this.props.getAllUser();
    }

  render(){
      let {users=[],error=null}=this.props;
      let {id}=this.state;
      if(error!=null) alert("delete successfully!!")
      return (
          
         <div>
          <Dashboard></Dashboard>
          <div>
              
              <div class="col-xs-9 col-sm-9 col-md-9 col-lg-9">
                 <div >
                   
                    <div >
                        <h2 style={{fontFamily:"'Times New Roman', Times, serif"}}>Manager Users</h2>
                        <hr style={{width:"300px",marginLeft:"7px",marginTop:"5px",backgroundColor:"#000000"}}></hr>
                    </div >
                    
                    <button type="button" class="btn btn-primary" style={{backgroundColor:" brown"}}><Link to="./newuser" >Add User</Link></button>
                       
                    <table class="table table-striped" style={{fontSize:"20px",fontFamily:"Times New Roman', Times, serif"}}>
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Address</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                     {this.props.users.length>0 &&
                       this.props.users.map((item,i)=>{
                           return(
                            
                            <tr>  
                            <td>{i}</td>
                            <td>{item.id}</td>
                            <td>{item.user_name}</td>
                            <td>{item.address}</td>   
                            <td> <button className="btn" style={{backgroundColor:"#A52A2A"}}onClick={e =>{
                                        //  this.props.getOrderDetail(item.id);
                                        //  this.props.getOrderById(item.id);
                                        this.props.getUser(item.id);
                                         }
                                         }><Link to='./user'>Detail</Link></button>
                                |
                                <button className="btn" style={{backgroundColor:"#A52A2A"}}onClick={e =>{
                                        //  this.props.getOrderDetail(item.id);
                                        //  this.props.getOrderById(item.id);
                                        this.props.deleteUser(item.id);
                                        this.props.getAllUser();
                                         }
                                         }><Link to='./users'>Delete</Link></button></td>
                                

                            </tr>
                       

                           )
                       }
                       )
                     }

                    </tbody>
                    </table>
                </div>
            </div>
              
            
      </div>
      </div>
  );
}
}
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
    
    return {
        users:state.userState.allUser,
        error:state.userState.error

    };
  }
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
      return {
          getAllUser:()=>dispatch(getAllUser()),
          getUser:(id)=>dispatch(getUser(id)),
          deleteUser:(id)=>dispatch(deleteUser(id)),
        //    getOrders:() =>dispatch(getOrderHistory()),
        //    getOrderDetail:(id)=>dispatch(getOrderDetail(id)),
        //    getOrderById:(id)=>dispatch(getOrderById(id)),
         
    };
      }     
export default connect(mapStateToProps,mapDispatchToProps)(ManagerUser);
