import {Link} from 'react-router-dom';
import { connect } from 'react-redux';
class Menu extends React.Component{
    constructor(props)
    {
        super(props);
        this.state = {value:"",by:""};
        this.change=this.change.bind(this);
    }
    componentDidMount()
    {  this.props.getSubCategory();
        this.props.getAllCategory();
    }
