import React, { Component } from 'react';
import { connect } from 'react-redux';
import {  getAllCategory,getAllSubCategory} from '../../redux/home_reducer';
import {getHotProduct} from '../../redux/hot_product_reducer';
import {getAllProduct} from '../../redux/product_reducer';
import {show} from '../../redux/product_detail_reducer';
import { Redirect,Link} from "react-router-dom";
import { actAddToCart} from '../../redux/cart_reducer';
import { FacebookProvider, Comments } from 'react-facebook';
import Menu from '../Menu/Menu.js';
import "./Home.css";
import {addToRecentlyViewed} from '../../redux/recent_reducer';
class Home extends Component{
    constructor(props) {
        super(props);
        this.state = {};
        this.onLoad = this.onLoad.bind(this);
        this.showDetail=this.showDetail.bind(this);
        this.onAddToCart=this.onAddToCart.bind(this);
      }
     componentWillMount()
     {
      this.props. getAllCategory();
      this.props.getHotProduct();
      this.props.getAllProduct();
      this.props.getAllSubCategory();
     }
      render(){
        
        let{ list_cate,list_hot_product,list_product,list_sub}=this.props;
        let {product_id,addProduct,id}=this.state;
        console.log(this.props);
          return(<div>
       
        
       <div class="col-xs-3 col-sm-3 col-md-3 col-lg-3">
            <Menu/> 
    </div>
    <div class="col-xs-9 col-sm-9 col-md-9 col-lg-9">
            <div class="category-featured digital">
  {list_hot_product.length>0 && <div>
  <nav class="navbar nav-menu show-brand">
    <div class="navbar-brand">
     Hot Product
    </div>
    <div class="nav navbar-right get-more">
     {/* get more */}
    </div>
  </nav>
       <div className="product-featured clearfix">
               <div className="row"></div>
               <div className="col-sm-12 category-product"></div>
                 {   
                  this.props.list_hot_product.map((item, i)=>{
                      let a =String(item.product_image);
                      return(
                       
                        <div className="col-sm-4">
                        <div className="product-item">
                          <div className="pi-img-wrapper">

                          <img className="img-responsive"   src={require('../../assets/'+item.product_image)} alt=""/>
                            <div>
                            <button className="btn" onClick={e =>{
                            this.props.show(item.id);
                            this.props.addToRecentlyViewed(item);
                            }
                            }><Link to='./product_detail'>Detail of Product</Link></button>
                            </div>
                          </div>
                          <h3>
                          <Link to='./product_detail'>{item.product_name}</Link>
                          </h3>
                          <div className="pi-price">
                           Price:{item.price}
                           <span> <button type="submit" className="shoe-cart pshoe-cart" onClick={e=>{this.props.onAddToCart(item)}}style={{width:"22%"}}><i className="fa fa-cart-plus" aria-hidden="true" /></button></span>               
                          </div>
                          
                         
                        </div>
                      </div>
                

                      )
                 })
               
                }
                </div>
                </div>
         }       
        
        { list_cate.length>0 &&<div>  
          {
         this.props.list_cate.map((item, i)=>
            { return(
          <div>
         <nav class="navbar nav-menu show-brand">
          <div class="navbar-brand" >
           {item.category_name}
          </div>
          <div class="nav navbar-right get-more"> {/* get more */}
            </div>
           </nav>
           <div className="product-featured clearfix">
               <div className="row"></div>
               <div className="col-sm-12 category-product"></div>
            { this.props.list_sub.map((item1,i)=>
              {if(item1.parent_id==item.id)
                   return(
                   <div>
               { this.props.list_product.map((item2,i)=>
                 { if (item1.id==item2.category_id)  return (
                  <div className="col-sm-4">
                  <div className="product-item">
                    <div className="pi-img-wrapper">

                    <img className="img-responsive"   src={require('../../assets/'+item2.product_image)} alt=""/>
                      <div>
                       <button className="btn" onClick={this.showDetail}><Link to='./product_detail'>Detail of Product</Link></button>
                      </div>
                    </div>
                    <h3>
                    <Link to='./product_detail'>{item2.product_name}</Link>
                    </h3>
                    <div className="pi-price">
                     Price:{item2.price}
                     <span> <button type="submit" className="shoe-cart pshoe-cart" onClick={this.onAddToCart}style={{width:"22%"}}><i className="fa fa-cart-plus" aria-hidden="true" /></button></span>               
                    </div>
                    
                   
                  </div>
                </div>

                         )
                    
                 })

               }
              
            </div>
            
                   )})}

        
         </div>











           </div>
            );
           
           })

           }
         
 </div>
         }
     
         </div>
                </div>

     </div>
        
          );
 }

 showDetail(e)
 {
   e.preventDefault();
  let { product_id,addProduct } = this.state;//lay trang thai off state do qua email va password
   this.props.show(product_id);
   this.props.addToRecentlyViewed(addProduct);
 
 }
 
 onAddToCart(e)
 {
   e.preventDefault();
   let {addProduct}=this.state;
   this.props.addToCart(addProduct);
 }
 onLoad(e)
{ e.preventDefault();
 
  this.props. getAllCategory();
  this.props.getHotProduct();
  this.props.getAllProduct();
}
}
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
  console.log(state);
    return {
     list_cate:state.homeState.allCategory,
     list_hot_product:state.hotProductState.hotProduct,
     list_product:state.productState.allProduct,
     list_sub:state.homeState.allSubCategory,
 };
}
    const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
        return {
          getAllCategory: () => dispatch(getAllCategory()),
          getHotProduct:()=>dispatch(getHotProduct()),
          show:(product_id)=>dispatch(show(product_id)),
          addToCart:(addProduct)=>dispatch(actAddToCart(addProduct,1)),
          addToRecentlyViewed:(addProduct)=>dispatch(addToRecentlyViewed(addProduct)),
          getAllProduct:()=>dispatch(getAllProduct()), 
          getAllSubCategory:()=>dispatch(getAllSubCategory())
          //action la login voi 2 tham so la email va password
        };
        }
 export default connect(mapStateToProps, mapDispatchToProps)(Home);
