import React from 'react';
import './Signup.css';
import { Link } from "react-router-dom";
import { connect } from 'react-redux';
import { signup } from '../../redux/signup_reducer';

class Signup extends React.Component{
  constructor(props){
    super(props);
    this.state={};
    this.onSubmit = this.onSubmit.bind(this);

  }
 
 
 render(){
  let {user_name,email,password,address}= this.state;//state co 2 trang thai la email va password
  let {isSignupPending, isSignupSuccess, signupError} = this.props;
  return (
    <div>
     
      
     <div className="container mt-30" style={{marginLeft:"110px"}}>        
          <div className="col-xs-10 col-sm-10 col-md-10 col-lg-10" style={{paddingTop:"20px"}}>
              
              
              <div className="row">
                <div style = {{paddingLeft:"240px"}}>
                  <h1 className="labelSignUp" style={{paddingLeft:"55px"}}>Sign Up</h1>
                </div>
              </div>
              
              
                
              <div className="row">
                <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
                   
                   <div >
                     <label >Username: </label>
                     <input type="text" className="form-control" name="Username"  onChange={e => this.setState({user_name: e.target.value})} value={user_name}  placeholder="UserName"/>
                     <br/>
                   </div>
                   <div className="form-group">
                     <label >Email: </label>
                     <input type="text" className="form-control" name="Email"   onChange={e => this.setState({email: e.target.value})} value={email} placeholder="Email" />
                   </div>
                   <div className="form-group">
                     <label >Address: </label>
                     <input type="text" className="form-control" name="Address"  onChange={e => this.setState({address: e.target.value})} value={address} placeholder="Address"/>
                   </div>
                 
                   <div className="form-group">
                     <label >Password: </label>
                     <input type="password" className="form-control" name="Password" onChange={e => this.setState({password: e.target.value})} value={password}  placeholder="Password"/>
                   </div>
                   <div className="form-group">
                     <label >Password Confimation: </label>
                     <input type="password" className="form-control" name="Password_Confimation"  placeholder="Password Confimation"/>
                   </div>
                  <div>
                  <span> <button  style={{marginLeft:"300px",marginTop:"20px"}}type="submit" className="btn btn-primary" onclick={this.onSubmit} >Register</button></span>
                   <span>
                      Do have an account yet? &nbsp;
                     <Link to="./login">Login now!!</Link>
                  
                  </span>
                  </div>
             </div>
             
             
             
             <div className="col-xs-1 col-sm-1 col-md-1 col-lg-1">
             <hr style={{width:"1px",height:"320px",backgroundColor:"#DCDCDC"}}></hr>
             </div>
             
             <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4" style={{marginTop:"90px"}}>
                <div style={{marginBottom:"20px",fontSize:"30px"}}>
                   <div style={{float:"left",color:"#0000CD"}}className="iconFB"><i className="fab fa-facebook-square"></i></div>
                   <button style={{marginLeft:"10px",fontSize:"13px"}}type="button" className="btn btn-sm btn-primary">Continue with Facebook</button>
               </div>
               
               <div  style={{marginTop:"30px",fontSize:"30px"}}>
                 
                 <img style={{width:"11%"}} src="https://yt3.ggpht.com/a/AGF-l7-BBIcC888A2qYc3rB44rST01IEYDG3uzbU_A=s900-c-k-c0xffffffff-no-rj-mo" />
                 
                 <button type="button" className="btn btn-primary" style={{marginLeft:"10px",fontSize:"13px"}}>Continue with Google</button>
                
               </div>
               
               </div>
          </div>
                
          <div className="message">
           {
             isSignupSuccess && <div>Success</div>
           }
          </div>
      
     </div>
  
     </div>
    </div>
  
       
  );
  }

onSubmit(e) {
  e.preventDefault();
  let { user_name,email, password,address } = this.state;//lay trang thai off state do qua email va password
  this.props.login(user_name,email,password ,address);//tao 1 props goi la login(email,password)
  this.setState({
    user_name:'',
    email: '',
    password: '',
    address:''
  });
}
}

const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
return {
  //isSignupPending: state.signupState.isSignupPending,
  isSignupSuccess: state.signupState.isSignupSuccess,
  signupError: state.signupState.signupError
};
}

const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
return {
  signup: (user_name,email,password,address) => dispatch(signup(user_name,email, password,address))//action la login voi 2 tham so la email va password
};
}
export default connect(mapStateToProps, mapDispatchToProps)(Signup);
